mapper.insert:
insert into house(id,

)
values((select nvl(max(id),0) from house )+1,

)

mapper.select:


	 select * from 
      ( 
         select rownum num ,a.* from 
           (
             select id,name,des 
             from house where status is null
             order by id
            )a
       )b 
       where b.num>=#{0} and  b.num<=#{1}
