mapper.select:
select * from ( select rownum rn,tb.* from ( select e.employee_id,e.employee_name,e.employee_loginname,e.employee_sex,e.employee_tel,e.employee_level,e.employee_attribute,p.position_name,a.area_name from employee e,position p,department d,area a where e.position_id=p.position_id and p.department_id=d.department_id and d.area_id=a.area_id
and e.employee_name like '%${employee_name}%'
order by employee_id desc )tb )a where rn between #{startrow,jdbcType=INTEGER} and #{endrow,jdbcType=INTEGER}

mapper.update:
update Employee
set EMPLOYEE_NUM = #{employee_num}
where
EMPLOYEE_ID = #{employee_id}
